try:
            ps_script = f'''
            Add-Type -TypeDefinition @"
            using System;
            using System.Runtime.InteropServices;
            
            [Guid("5CDF2C82-841E-4546-9722-0CF74078229A"), InterfaceType(ComInterfaceType.InterfaceIsIUnknown)]
            public interface IAudioEndpointVolume {{
                int f(); int g(); int h(); int i();
                int SetMasterVolumeLevelScalar(float fLevel, System.Guid pguidEventContext);
                int j();
                int GetMasterVolumeLevelScalar(out float pfLevel);
                int k(); int l(); int m(); int n();
                int SetMute([MarshalAs(UnmanagedType.Bool)] bool bMute, System.Guid pguidEventContext);
                int GetMute(out bool pbMute);
            }}
            
            [Guid("D666063F-1587-4E43-81F1-B948E807363F"), InterfaceType(ComInterfaceType.InterfaceIsIUnknown)]
            public interface IMMDevice {{
                int Activate(ref System.Guid id, int clsCtx, int ActivParams, out IAudioEndpointVolume aev);
            }}
            
            [Guid("A95664D2-9614-4F35-A746-DE8DB63617E6"), InterfaceType(ComInterfaceType.InterfaceIsIUnknown)]
            public interface IMMDeviceEnumerator {{
                int GetDefaultAudioEndpoint(int dataFlow, int role, out IMMDevice ppDevice);
            }}
            
            [ComImport, Guid("BCDE0395-E52F-467C-8E3D-C4579291692E")]
            public class MMDeviceEnumeratorComObject {{ }}
            
            public class Audio {{
                public static void SetVolume(float v) {{
                    var enumerator = new MMDeviceEnumeratorComObject() as IMMDeviceEnumerator;
                    IMMDevice dev = null;
                    enumerator.GetDefaultAudioEndpoint(0, 1, out dev);
                    
                    var volId = typeof(IAudioEndpointVolume).GUID;
                    IAudioEndpointVolume epv = null;
                    dev.Activate(ref volId, 23, 0, out epv);
                    
                    epv.SetMasterVolumeLevelScalar(v, [System.Guid]::Empty);
                }}
                
                public static void SetMute(bool mute) {{
                    var enumerator = new MMDeviceEnumeratorComObject() as IMMDeviceEnumerator;
                    IMMDevice dev = null;
                    enumerator.GetDefaultAudioEndpoint(0, 1, out dev);
                    
                    var volId = typeof(IAudioEndpointVolume).GUID;
                    IAudioEndpointVolume epv = null;
                    dev.Activate(ref volId, 23, 0, out epv);
                    
                    epv.SetMute(mute, [System.Guid]::Empty);
                }}
            }}
"@
            [Audio]::SetVolume([float]{level / 100.0})
            if ($?) {{ Write-Output "Success" }} else {{ exit 1 }}
            '''
